     // Set the date we're counting down to
var countDownDate = new Date("Oct 17, 2018 16:20:20").getTime();

// Update the count down every 1 second
var x = setInterval(function() {

    // Get todays date and time
    var now = new Date().getTime();
    
    // Find the distance between now an the count down date
    var distance = countDownDate - now;
    
    // Time calculations for days, hours, minutes and seconds
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);
    
    // Output the result in an element with id="demo"
    document.getElementById("count").innerHTML = days + "d " + hours + "h " + minutes + "m " + seconds + "s ";
    document.getElementById("count1").innerHTML ="Available in "+"<br>" + days + "d " + hours + "h " + minutes + "m " + seconds + "s ";
    document.getElementById("count2").innerHTML ="Available in "+"<br>"  + days + "d " + hours + "h " + minutes + "m " + seconds + "s ";
    document.getElementById("count3").innerHTML ="Available in "+"<br>"  + days + "d " + hours + "h " + minutes + "m " + seconds + "s ";
    
    // If the count down is over, write some text 
    if (distance < 0) {
        clearInterval(x);
        document.getElementById("count").innerHTML = "Happy Legalization Canada";
        document.getElementById("finishCount").innerHTML = "";
    }
    if (distance < 0) {
        clearInterval(x);
        document.getElementById("count1").innerHTML = "1";
        document.getElementById("count2").innerHTML = "2";
        document.getElementById("count3").innerHTML = "3";
    }
}, 1000);
